<script>
import mainLayout from "@/layouts/main.vue";
import '../script.js'
import clientFooter from "@/views/includes/footer.vue";
import {useCounter, useIdle} from "@vueuse/core";
import {watch} from "vue";

export default {
    name: 'App',
    data(){
        return {
            menuShow : false,
            menuApiUrl:'/api/get-menu',
            menu:'menu',
            menuName:'Main Menu',
            dataDetails:{},
            mobileMenuDetails: {},
            names:{},
            svgs:{},
            subMenu:'',
            audio:'./prompts/audio.mp3'
        }
    },
    components: {
        clientFooter,
     mainLayout
    },
    mounted() {
        this.init()
        this.mobileMenuDetails = this.dataDetails
    },
    created() {
        const { inc, count } = useCounter()

        const { idle, lastActive, reset } = useIdle(30 * 1000) // 5 min

        watch(idle, (idleValue) => {
            if (idleValue) {
                inc()
                this.audio = ''
                this.$router.push({name:'home'})
                reset() // restarts the idle timer. Does not change lastActive value
            }
        })
    },
    methods:{
        openNav(){
            this.menuShow = true
        },
        closeNav()
        {
            this.menuShow = false
        },
        goToAudioTest()
        {
            this.$router.push({name:'test'})
        },
        getMenu()
        {
            return new Promise((resolve, reject) => {
                axios
                    .post(this.menuApiUrl, {menu:this.menu})
                    .then((response) => {
                        resolve(response)
                    })
                    .catch((error) => {
                        reject(error)
                    })
                    .finally(() => {
                    })
            })
        },
        async init()
        {
            await this.getMenu().then(response => {
                this.dataDetails = response.data.elements
                this.audio = response.data.audio
            })
        },
        getMenuDetails(name)
        {
            this.menu = name
            this.menuName = name
            this.init()

        }

    }

}

</script>

